sys_script_include.script:
function SASToken(resourceUri, sasKeyName, sasKey) {

	if (!resourceUri || !sasKeyName || !sasKey) {
        throw "Missing required parameter";
    }

    const encoded = encodeURIComponent(resourceUri);
    const now = new Date();
    const minute = 240;
    const ttl = Math.round(now.getTime() / 1000) + minute;
    const signature = encoded + '\n' + ttl;

    const encrypted = CryptoJS.HmacSHA256(signature, sasKey);
    const hash = HexToBase64(encrypted);

    return 'SharedAccessSignature sr=' + encoded + '&sig=' + encodeURIComponent(hash) + '&se=' + ttl + '&skn=' + sasKeyName;
}
